CustomField.formula:
IF(OR(ISPICKVAL(FSM_BusinessUnit__c, 'Waste Non Infra'), ISPICKVAL(FSM_BusinessUnit__c, 'Water Non Infra')) , IF(ISPICKVAL(Status, 'Completed'), DATEVALUE(FSM_JobCompletionDate__c) - DATEVALUE(StartDate), TODAY() - DATEVALUE(StartDate)) ,
IF(OR(ISPICKVAL(FSM_BusinessUnit__c, 'Waste Infra'), ISPICKVAL(FSM_BusinessUnit__c, 'Metering'), ISPICKVAL(FSM_BusinessUnit__c, 'Water Infra')) , IF(ISPICKVAL(Status, 'Completed'), DATEVALUE(FSM_JobCompletionDate__c) - DATEVALUE(FSM_JobCreatedDate__c), TODAY() - DATEVALUE(FSM_JobCreatedDate__c)) ,
IF(ISPICKVAL(FSM_BusinessUnit__c, 'Developer Services') , IF(ISPICKVAL(Status, 'Completed'), FSM_InspectionPassedDate__c - FSM_ConnectionPlannedDate__c , TODAY() - DATEVALUE(FSM_ConnectionPlannedDate__c)) , 0
)
)
)
